catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    //Type appropriate comment here, and begin script below
    var requestedFor = g_form.getValue("vs_rq_requested_for"); //get the item sys_id of the item
    var catItemID = g_form.getUniqueValue(); //get the item requested for user value 

    var quantityGA = new GlideAjax("quantityLimitUtils"); ////call the script include by using the glideAjax call
    quantityGA.addParam('sysparm_name', 'priceValue'); //call the script include function name
    quantityGA.addParam('sysparm_user', requestedFor); //send the user value as a parameter to the function
    quantityGA.addParam('sysparm_id', catItemID); //send the catalog item as parameter to the function
    quantityGA.getXML(limitValue); //call the callback function


    function limitValue(response) { //define the callback function & send response as parameter
        var answer = response.responseXML.documentElement.getAttribute("answer"); //get the asnwer attribute
        var itemDetails = JSON.parse(answer); //by using JSON parse get the values
        var price = itemDetails.price; //dot wall the object & retrive the price value
        var recurringPrice = itemDetails.recurringPrice; //dot wall the object & retrive the recurringprice value
        var recurringSchedule = itemDetails.recurringFrequence; //dot wall the object & retrive the recurringFrequence value
        var locationLimit = itemDetails.locationCreditLimit; //get the user location credit limit value
        var totalPrice = price * newValue; //calculate the total priceVlaue
        var totalRecurringPrice = recurringPrice * newValue; //calculate the total recurringPrice value
        var totalPriceValue;

        if (locationLimit < totalPrice) { //check the location limit is not greater than total price
            //remove the quantity & total price value & throw the error message to the user
            g_form.clearValue('vs_rq_quantity');
            g_form.clearValue('vs_rq_total_price');
            g_form.addErrorMessage("Credit Limit for your location is $ " + locationLimit + " You have selected for $ " + totalPrice + " Please order the request below your location credit limit");

        } else {
            if (recurringPrice == "0") { //check the recurringPrice price value is 0
                //build the string & assign it to the total price variable
                totalPriceValue = "$ " + totalPrice;
                g_form.setValue("vs_rq_total_price", totalPriceValue);
            } else {
                //build the string & assign it to the total price variable
                totalPriceValue = "$ " + totalPrice + "+" + totalRecurringPrice + recurringSchedule;
                g_form.setValue("vs_rq_total_price", totalPriceValue);
            }
        }
    }

}
