catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var catItemID = g_form.getUniqueValue();//get the item sys_id by using the g_form API
    var quantityGA = new GlideAjax("quantityLimitUtils"); //call the script include by using the glideAjax call
    quantityGA.addParam('sysparm_name', 'catItemOrderLimit');//call the script include function name
    quantityGA.addParam('sysparm_id', catItemID);//send the catalog item as parameter to the function
    quantityGA.getXML(limitValue); //call the callback function

    function limitValue(response) {//define the callback function & send response as parameter
        var answer = response.responseXML.documentElement.getAttribute("answer");//get the asnwer attribute
        var quantityValue = parseInt(answer);//change the values into int type

        var emptyValue = 0;//create a empty variable 
        var arrayValue = [];//create a empty array
        for (var i = 0; i < quantityValue; i++) {//check the item on-hand value length & assign the value to the emptyValue variable by using Push method
            emptyValue = emptyValue + 1;
            arrayValue.push(emptyValue);
        }
        var arrayLength = arrayValue.length;
        for (var j = 0; j < arrayLength; j++) { //assign the item on-hand value to the quantity variable by using addOption method
            g_form.addOption("vs_rq_quantity", arrayValue[j], arrayValue[j]);
        }
		//verify the value is empty, if value is empty throw the error message
        if (arrayLength == 0) {
            g_form.addErrorMessage("This item is currently not available, please request a item once it back");
		}
	}
}
