sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Get the location variable value
    var location = current.variables.vs_rq_requested_for.location;
    // Initialize a GlideRecord for the credit limit table query the location column with requested for user location
    var locationGR = new GlideRecordSecure("x_898369_ava_cre_0_location_credit_limit");
    locationGR.addQuery('x_avaso_ava_credit_location', location);
    locationGR.query();
    // Check if the location record is available
    if (locationGR.next()) {
        // Access the credit limit field value and update the actual credit limit of the item
        locationGR.x_avaso_ava_credit_credit_limit = locationGR.x_avaso_ava_credit_credit_limit - (current.price * current.quantity);
        //update the record
        locationGR.update();
    }

    // Initialize a GlideRecord for the item on-hand value table and query the item column with user submitted item
    var catItemGR = new GlideRecordSecure("x_898369_ava_cre_0_item_quantity");
    catItemGR.addQuery('x_avaso_ava_credit_item', current.cat_item);
    catItemGR.query();
    // Check if the catalog item record is available
    if (catItemGR.next()) {
        // Access the onhand value field, Subtract the quantity from the catalog item's onhand value
        catItemGR.x_avaso_ava_credit_on_hand_value = catItemGR.x_avaso_ava_credit_on_hand_value - current.quantity;
        // Update the record
        catItemGR.update();
    }



})(current, previous);
